import React, { useState, useEffect } from 'react';
import UserForm from './components/UserForm';
import Nav from './components/Navigation';
import TabBar from './components/TabBar';
import Login from './screens/Login';
import Register from './screens/Register';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'

import { getAuthedUser, setLocalStorage } from './utils/api';

// import TrialModal from './components/TrialModal';

function App() {

    const [authedUser, setAutherUser] = useState({
        id: null,
        userToken: null,
        userType: null,
    })

    const [authedUserDetails, setAuthedUserDetails] = useState(null);

    useEffect(() => {
        async function getUser() {
            let user = await getAuthedUser();
            console.log(user.userDetails)
            setAutherUser(user.authedUser);
            setAuthedUserDetails(user.userDetails);
        }
        getUser();
    }, [])

    const updateAuthedUser = (response) => {
        console.log(response)
        let user = {
            id: response.user._id,
            userToken: response.token,
            userType: response.user.isadmin === false ? 'user' : 'admin',
        }
        setAuthedUserDetails(response.user);
        setLocalStorage(user);
        setAutherUser(user);
    }

    const handleLogout = () => {
        let user = {
            id: null,
            userToken: null,
            userType: null,
        }
        setAutherUser(user);
        setAuthedUserDetails(null);
        setLocalStorage(user);
    }

    return (
        <Router>
            <div className='container'>
                {authedUser.id === null ? (
                    <Switch>
                        <Route exact path='/' render={() => (
                            <Login
                                updateAuthedUser={updateAuthedUser}
                            />
                        )}
                        />)
                        <Route exact path="/register" component={Register} />
                    </Switch>
                ) : (
                        <>
                            <Nav handleLogout={handleLogout} />
                            <Switch>
                                <Route exact path='/' render={() => (
                                    <TabBar
                                        authedUserDetails={authedUserDetails}
                                    />
                                )}
                                />
                                <Route exact path="/add" component={UserForm} />
                                {/* <Route component={Error} /> */}
                            </Switch>
                        </>
                    )}
            </div>
        </Router>
    );
}

export default App;
