import React, { useState } from 'react';
import { Card, Button, Form } from 'react-bootstrap';
import userForm from '../components/UserForm';
import { Link } from 'react-router-dom'

const Login = () => {

    const [loginPage, setLoginPage] = useState(true);

    const [loginDetails, setLoginDetails] = useState({
        email: '',
        password: '',
    })

    const handleChange = (e, field) => {
        if (field === 'email') {
            setLoginDetails({
                ...loginDetails,
                email: e.target.value
            })
        }

        if (field === 'password') {
            setLoginDetails({
                ...loginDetails,
                password: e.target.value
            })
        }
    }

    const showRegisterPage = () => {
        setLoginPage(false);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(loginDetails);
    }

    return (
        <Card>
            <Card.Header as="h5">{loginPage ? "Login" : "Register"}</Card.Header>
            <Card.Body>
                {loginPage && (
                    <>
                        <Form onSubmit={handleSubmit}>
                            <Form.Group>
                                <Form.Label>Email</Form.Label>
                                <Form.Control
                                    required
                                    type="email"
                                    placeholder="Enter email"
                                    onChange={(e) => handleChange(e, 'email')}
                                    name="email"
                                    value={loginDetails.email}
                                />
                            </Form.Group>

                            <Form.Group controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control
                                    required
                                    type="password"
                                    placeholder="Password"
                                    onChange={(e) => handleChange(e, 'password')}
                                    name="password"
                                    minLength="8"
                                    value={loginDetails.password}
                                />
                            </Form.Group>
                            <Button variant="primary" type="submit">
                                Submit
                            </Button>
                        </Form>
                        <Link to={`/register`}>
                            <Button color='purple' onClick={this.handleOnClick}>{answered === "true" ? "View Poll" : "View Results"}</Button>
                        </Link>
                        <Card.Link onClick={showRegisterPage}>Not an existing user, Register here!</Card.Link>
                    </>)
                }
                {
                    loginPage == false && (
                        <UserForm />
                    )
                }
            </Card.Body>
        </Card>
    );
}

export default Login;
